import { createSlice } from '@reduxjs/toolkit';

export const todosSlice = createSlice({
  name: 'todos',
  initialState: {
    items: [{ id: 1, title: 'Buy groceries', completed: false }],
  },
  reducers: {},
});

export default todosSlice.reducer;
